import React, { useState, useEffect } from 'react';
import './style.css';

export function App() {
    const [stage, setStage] = useState('selection'); // 'selection', 'scanning', 'results'
    const [scanTokens, setScanTokens] = useState(5);
    const [scanProgress, setScanProgress] = useState(0);
    const [scanningMessage, setScanningMessage] = useState('');
    const [selectedType, setSelectedType] = useState('');
    const [inputUrl, setInputUrl] = useState('');
    const [securityScore, setSecurityScore] = useState(75);
    const [expandedRiskCategory, setExpandedRiskCategory] = useState(null);

    const vulnerabilities = [
        {
            severity: 'high',
            title: 'Exposed SSL Certificate',
            description: 'Your SSL certificate is visible',
            recommendation:
                'Store SSL certificates in secure environment variables or a key management service',
        },
        {
            severity: 'medium',
            title: 'Open Ports Detected',
            description: 'Ports 80 and 443 are exposed to public access',
            recommendation:
                'Configure your firewall to restrict access to necessary services only',
        },
        {
            severity: 'low',
            title: 'Cookie Security',
            description: 'Secure flag not set on session cookies',
            recommendation:
                'Enable secure flag for all session cookies in your application settings',
        },
    ];

    const scanningMessages = [
        'Initiating security scan...',
        'Examining scripts and libraries...',
        'Analyzing web server configuration...',
        'Analyzing SSL certificates...',
        'Scanning for open ports...',
        'Examining cookie security...',
        'Assessing input validation...',
        'Evaluating authentication mechanisms...',
        'Analyzing database connections...',
        'Examining session management...',
        'Checking for cross-site scripting (XSS)...',
        'Analyzing server-side validation...',
        'Scanning for SQL injection...',
        'Examining directory traversal protections...',
        'Analyzing encryption usage...',
        'Compiling vulnerability report...',
    ];

    useEffect(() => {
        let progressInterval;
        let messageInterval;

        if (stage === 'scanning') {
            // Simulate progress and messages
            let progress = 0;
            let messageIndex = 0;

            progressInterval = setInterval(() => {
                progress += 3;
                setScanProgress(progress);

                if (progress >= 100) {
                    clearInterval(progressInterval);
                    clearInterval(messageInterval);
                    setStage('results');
                }
            }, 500);

            messageInterval = setInterval(() => {
                if (messageIndex < scanningMessages.length) {
                    setScanningMessage(scanningMessages[messageIndex]);
                    messageIndex++;
                }
            }, 1000);
        }

        return () => {
            if (progressInterval) clearInterval(progressInterval);
            if (messageInterval) clearInterval(messageInterval);
        };
    }, [stage]);

    const handleStartScan = () => {
        if (scanTokens > 0) {
            setScanTokens(prev => prev - 1);
            setStage('scanning');
        } else {
            alert('No scan tokens remaining. Please upgrade.');
        }
    };

    const renderSecurityScoreIndicator = () => {
        // Convert 75 to a color from green to red
        const greenToRed = `rgb(${255 * (1 - securityScore / 100)}, ${
            255 * (securityScore / 100)
        }, 0)`;
        return (
            <div
                style={{
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    backgroundColor: greenToRed,
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    color: 'white',
                    fontWeight: 'bold',
                    fontSize: '24px',
                }}
            >
                {securityScore}
            </div>
        );
    };

    const renderRiskCategories = () => {
        const riskCategories = [
            {
                label: 'High Risk',
                count: vulnerabilities.filter(v => v.severity === 'high').length,
                color: '#ef4444',
                risks: vulnerabilities.filter(v => v.severity === 'high'),
            },
            {
                label: 'Medium Risk',
                count: vulnerabilities.filter(v => v.severity === 'medium').length,
                color: '#f59e0b',
                risks: vulnerabilities.filter(v => v.severity === 'medium'),
            },
            {
                label: 'Low Risk',
                count: vulnerabilities.filter(v => v.severity === 'low').length,
                color: '#3b82f6',
                risks: vulnerabilities.filter(v => v.severity === 'low'),
            },
        ];

        return (
            <div style={{ marginTop: '20px' }}>
                <div
                    style={{
                        display: 'grid',
                        gridTemplateColumns: 'repeat(3, 1fr)',
                        gap: '10px',
                    }}
                >
                    {riskCategories.map((category, index) => (
                        <div
                            key={index}
                            style={{
                                border: `1px solid ${category.color}`,
                                borderRadius: '8px',
                                padding: '10px',
                                cursor: 'pointer',
                            }}
                            onClick={() =>
                                setExpandedRiskCategory(
                                    expandedRiskCategory === category.label
                                        ? null
                                        : category.label
                                )
                            }
                        >
                            <div style={{ color: category.color, fontWeight: 'bold' }}>
                                {category.label}
                            </div>
                            <div>{category.count} Vulnerabilities</div>
                        </div>
                    ))}
                </div>
                {/* Expanded Risk Details */}
                {expandedRiskCategory && (
                    <div style={{ marginTop: '20px' }}>
                        <h3>{expandedRiskCategory} Vulnerabilities</h3>
                        {riskCategories
                            .find(cat => cat.label === expandedRiskCategory)
                            .risks.map((risk, index) => (
                                <div
                                    key={index}
                                    style={{
                                        border: '1px solid #e0e0e0',
                                        borderRadius: '8px',
                                        padding: '10px',
                                        marginBottom: '10px',
                                    }}
                                >
                                    <h4>{risk.title}</h4>
                                    <p>{risk.description}</p>
                                    <strong>Recommendation:</strong>
                                    <p>{risk.recommendation}</p>
                                </div>
                            ))}
                    </div>
                )}
            </div>
        );
    };

    // Shared container style
    const containerStyle = {
        maxWidth: '800px',
        margin: '0 auto',
        padding: '20px',
        backgroundColor: '#f8fafc',
        borderRadius: '12px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        textAlign: 'center',
    };

    // Shared button style
    const buttonStyle = {
        padding: '10px 20px',
        backgroundColor: '#2563eb',
        color: 'white',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        transition: 'background-color 0.3s ease',
    };

    // Selection Stage
    if (stage === 'selection') {
        return (
            <div style={containerStyle}>
                <h1 style={{ color: '#1e40af', marginBottom: '20px' }}>SaaSpect</h1>
                <p style={{ marginBottom: '20px', fontWeight: 'bold' }}>
                    Scan Tokens Remaining:{' '}
                    <span style={{ color: '#2563eb' }}>{scanTokens}</span>
                </p>

                <div style={{ marginBottom: '20px' }}>
                    <div style={{ marginBottom: '10px', fontWeight: 'bold' }}>
                        Select Scan Type:
                    </div>
                    <div
                        style={{ display: 'flex', justifyContent: 'center', gap: '20px' }}
                    >
                        <button
                            onClick={() => setSelectedType('localhost')}
                            style={{
                                ...buttonStyle,
                                backgroundColor:
                                    selectedType === 'localhost' ? '#2563eb' : '#e2e8f0',
                                color: selectedType === 'localhost' ? 'white' : '#2563eb',
                            }}
                        >
                            Localhost
                        </button>
                        <button
                            onClick={() => setSelectedType('hosted')}
                            style={{
                                ...buttonStyle,
                                backgroundColor:
                                    selectedType === 'hosted' ? '#2563eb' : '#e2e8f0',
                                color: selectedType === 'hosted' ? 'white' : '#2563eb',
                            }}
                        >
                            Hosted Website
                        </button>
                    </div>
                </div>

                {selectedType === 'hosted' && (
                    <input
                        type='text'
                        placeholder='Enter website URL'
                        value={inputUrl}
                        onChange={e => setInputUrl(e.target.value)}
                        style={{
                            width: '100%',
                            padding: '10px',
                            marginBottom: '20px',
                            borderRadius: '8px',
                            border: '1px solid #cbd5e1',
                        }}
                    />
                )}

                <button
                    onClick={handleStartScan}
                    disabled={!selectedType || (selectedType === 'hosted' && !inputUrl)}
                    style={{
                        ...buttonStyle,
                        opacity:
                            !selectedType || (selectedType === 'hosted' && !inputUrl)
                                ? 0.5
                                : 1,
                    }}
                >
                    Start Scanning
                </button>
            </div>
        );
    }

    // Scanning Stage
    if (stage === 'scanning') {
        return (
            <div style={containerStyle}>
                <h1 style={{ color: '#1e40af', marginBottom: '20px' }}>
                    Scanning in Progress
                </h1>
                <div
                    style={{
                        width: '100%',
                        height: '20px',
                        backgroundColor: '#e0e0e0',
                        borderRadius: '10px',
                        marginBottom: '20px',
                    }}
                >
                    <div
                        style={{
                            width: `${scanProgress}%`,
                            height: '100%',
                            backgroundColor: '#2563eb',
                            borderRadius: '10px',
                            transition: 'width 0.5s ease-in-out',
                        }}
                    ></div>
                </div>
                <p style={{ color: '#475569' }}>{scanningMessage}</p>
            </div>
        );
    }

    // Results Stage
    return (
        <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
            <div
                style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    marginBottom: '20px',
                }}
            >
                <h1 style={{ color: '#1e40af' }}>Security Scan Results</h1>
                <button
                    onClick={() => {
                        setStage('selection');
                        setExpandedRiskCategory(null);
                    }}
                    style={buttonStyle}
                >
                    New Scan
                </button>
            </div>

            <div
                style={{
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    marginBottom: '20px',
                }}
            >
                {renderSecurityScoreIndicator()}
            </div>

            {renderRiskCategories()}
        </div>
    );
}
export default App;
