import React, { useState, useEffect } from 'react';
import { Shield, Zap, Clock, Search, RefreshCw } from 'lucide-react';

export function App() {
    const [stage, setStage] = useState('selection');
    const [scanTokens, setScanTokens] = useState(5);
    const [scanProgress, setScanProgress] = useState(0);
    const [scanningMessage, setScanningMessage] = useState('');
    const [selectedType, setSelectedType] = useState('');
    const [inputUrl, setInputUrl] = useState('');
    const [securityScore, setSecurityScore] = useState(75);
    const [expandedRiskCategory, setExpandedRiskCategory] = useState(null);

    const vulnerabilities = [
        {
            severity: 'high',
            title: 'Exposed SSL Certificate',
            description: 'Your SSL certificate is visible',
            recommendation:
                'Store SSL certificates in secure environment variables or a key management service',
        },
        {
            severity: 'medium',
            title: 'Open Ports Detected',
            description: 'Ports 80 and 443 are exposed to public access',
            recommendation:
                'Configure your firewall to restrict access to necessary services only',
        },
        {
            severity: 'low',
            title: 'Cookie Security',
            description: 'Secure flag not set on session cookies',
            recommendation:
                'Enable secure flag for all session cookies in your application settings',
        },
    ];

    const scanningMessages = [
        'Initiating security scan...',
        'Examining scripts and libraries...',
        'Analyzing web server configuration...',
        'Analyzing SSL certificates...',
        'Scanning for open ports...',
        'Examining cookie security...',
        'Assessing input validation...',
        'Evaluating authentication mechanisms...',
        'Analyzing database connections...',
        'Examining session management...',
        'Checking for cross-site scripting (XSS)...',
        'Analyzing server-side validation...',
        'Scanning for SQL injection...',
        'Analyzing encryption usage...',
        'Compiling vulnerability report...',
    ];

    useEffect(() => {
        let progressInterval;
        let messageInterval;

        if (stage === 'scanning') {
            let progress = 0;
            let messageIndex = 0;

            progressInterval = setInterval(() => {
                progress += 3;
                setScanProgress(progress);

                if (progress >= 100) {
                    clearInterval(progressInterval);
                    clearInterval(messageInterval);
                    setStage('results');
                }
            }, 500);

            messageInterval = setInterval(() => {
                if (messageIndex < scanningMessages.length) {
                    setScanningMessage(scanningMessages[messageIndex]);
                    messageIndex++;
                }
            }, 1000);
        }

        return () => {
            if (progressInterval) clearInterval(progressInterval);
            if (messageInterval) clearInterval(messageInterval);
        };
    }, [stage]);

    const handleStartScan = () => {
        if (scanTokens > 0) {
            setScanTokens(prev => prev - 1);
            setStage('scanning');
        } else {
            alert('No scan tokens remaining. Please upgrade.');
        }
    };

    const renderSecurityScoreIndicator = () => {
        const getScoreColor = () => {
            if (securityScore >= 80) return 'bg-green-500';
            if (securityScore >= 50) return 'bg-yellow-500';
            return 'bg-red-500';
        };

        return (
            <div className={`
                w-48 h-48 rounded-full ${getScoreColor()} 
                flex items-center justify-center 
                text-white font-bold text-4xl 
                shadow-xl transition-all duration-300 
                hover:scale-105 hover:shadow-2xl
            `}>
                {securityScore}
            </div>
        );
    };

    const renderRiskCategories = () => {
        const riskCategories = [
            {
                label: 'High Risk',
                count: vulnerabilities.filter(v => v.severity === 'high').length,
                color: 'text-red-500 border-red-500',
                icon: <Shield className="text-red-500" />,
                risks: vulnerabilities.filter(v => v.severity === 'high'),
            },
            {
                label: 'Medium Risk',
                count: vulnerabilities.filter(v => v.severity === 'medium').length,
                color: 'text-yellow-500 border-yellow-500',
                icon: <Zap className="text-yellow-500" />,
                risks: vulnerabilities.filter(v => v.severity === 'medium'),
            },
            {
                label: 'Low Risk',
                count: vulnerabilities.filter(v => v.severity === 'low').length,
                color: 'text-blue-500 border-blue-500',
                icon: <Clock className="text-blue-500" />,
                risks: vulnerabilities.filter(v => v.severity === 'low'),
            },
        ];

        return (
            <div className="space-y-6">
                <div className="grid grid-cols-3 gap-4">
                    {riskCategories.map((category, index) => (
                        <div
                            key={index}
                            className={`
                                border ${category.color} rounded-lg p-4 
                                transition-all duration-300 
                                hover:bg-gray-50 hover:shadow-md 
                                cursor-pointer group
                            `}
                            onClick={() =>
                                setExpandedRiskCategory(
                                    expandedRiskCategory === category.label
                                        ? null
                                        : category.label
                                )
                            }
                        >
                            <div className="flex items-center justify-between">
                                <div>
                                    <div className={`font-bold ${category.color}`}>
                                        {category.label}
                                    </div>
                                    <div className="text-gray-600">
                                        {category.count} Vulnerabilities
                                    </div>
                                </div>
                                {category.icon}
                            </div>
                        </div>
                    ))}
                </div>

                {expandedRiskCategory && (
                    <div className="bg-white shadow-lg rounded-lg p-6 mt-4 animate-fade-in">
                        <h3 className="text-2xl font-semibold mb-4">{expandedRiskCategory} Details</h3>
                        {riskCategories
                            .find(cat => cat.label === expandedRiskCategory)
                            .risks.map((risk, index) => (
                                <div
                                    key={index}
                                    className="
                                        bg-gray-50 rounded-lg p-4 mb-4
                                        border border-gray-200
                                        transition-all duration-300
                                        hover:shadow-md
                                    "
                                >
                                    <h4 className="text-lg font-bold mb-2">{risk.title}</h4>
                                    <p className="text-gray-600 mb-2">{risk.description}</p>
                                    <div className="flex items-center">
                                        <strong className="mr-2">Recommendation:</strong>
                                        <p>{risk.recommendation}</p>
                                    </div>
                                </div>
                            ))}
                    </div>
                )}
            </div>
        );
    };

    // Selection Stage
    if (stage === 'selection') {
        return (
            <div className="min-h-screen bg-gray-100 flex items-center justify-center">
                <div className="
                    bg-white p-8 rounded-2xl shadow-2xl
                    w-full max-w-md
                    transform transition-all duration-300
                    hover:shadow-3xl
                ">
                    <div className="flex items-center justify-center mb-6">
                        <Shield className="w-12 h-12 text-blue-600 mr-2" />
                        <h1 className="text-3xl font-bold text-blue-800">SaaSpect</h1>
                    </div>

                    <div className="text-center mb-6">
                        <p className="text-gray-600">
                            Scan Tokens: <span className="font-bold text-blue-600">{scanTokens}</span>
                        </p>
                    </div>

                    <div className="space-y-6">
                        <div>
                            <h2 className="text-lg font-semibold text-gray-700 mb-4 text-center">
                                Select Scan Type
                            </h2>
                            <div className="flex justify-center space-x-4">
                                {['localhost', 'hosted'].map((type) => (
                                    <button
                                        key={type}
                                        onClick={() => setSelectedType(type)}
                                        className={`
                                            px-6 py-3 rounded-lg 
                                            transition-all duration-300 
                                            ${selectedType === type
                                            ? 'bg-blue-600 text-white'
                                            : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}
                                        `}
                                    >
                                        {type.charAt(0).toUpperCase() + type.slice(1)}
                                    </button>
                                ))}
                            </div>
                        </div>

                        {selectedType === 'hosted' && (
                            <input
                                type="text"
                                placeholder="Enter website URL"
                                value={inputUrl}
                                onChange={(e) => setInputUrl(e.target.value)}
                                className="
                                    w-full px-4 py-3
                                    border border-gray-300 rounded-lg
                                    focus:outline-none focus:ring-2 focus:ring-blue-500
                                "
                            />
                        )}

                        <button
                            onClick={handleStartScan}
                            disabled={!selectedType || (selectedType === 'hosted' && !inputUrl)}
                            className={`
                                w-full py-3 rounded-lg 
                                flex items-center justify-center
                                transition-all duration-300
                                ${(!selectedType || (selectedType === 'hosted' && !inputUrl))
                                ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                                : 'bg-blue-600 text-white hover:bg-blue-700'}
                            `}
                        >
                            <Search className="mr-2" /> Start Scanning
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    // Scanning Stage
    if (stage === 'scanning') {
        return (
            <div className="min-h-screen bg-gray-100 flex items-center justify-center">
                <div className="
                    bg-white p-8 rounded-2xl shadow-2xl
                    w-full max-w-md text-center
                ">
                    <h1 className="text-2xl font-bold text-blue-800 mb-6">
                        Scanning in Progress
                    </h1>
                    <div className="w-full bg-gray-200 rounded-full h-4 mb-6">
                        <div
                            className="bg-blue-600 h-4 rounded-full"
                            style={{ width: `${scanProgress}%` }}
                        />
                    </div>
                    <p className="text-gray-600">{scanningMessage}</p>
                </div>
            </div>
        );
    }

    // Results Stage
    return (
        <div className="min-h-screen bg-gray-100 py-12">
            <div className="
                bg-white max-w-4xl mx-auto
                rounded-2xl shadow-2xl p-8
            ">
                <div className="flex justify-between items-center mb-8">
                    <div className="flex items-center">
                        <Shield className="w-10 h-10 text-blue-600 mr-3" />
                        <h1 className="text-3xl font-bold text-blue-800">
                            Security Scan Results
                        </h1>
                    </div>
                    <button
                        onClick={() => {
                            setStage('selection');
                            setExpandedRiskCategory(null);
                        }}
                        className="
                            bg-blue-600 text-white
                            px-4 py-2 rounded-lg
                            flex items-center
                            hover:bg-blue-700
                            transition-colors
                        "
                    >
                        <RefreshCw className="mr-2" /> New Scan
                    </button>
                </div>

                <div className="flex justify-center mb-8">
                    {renderSecurityScoreIndicator()}
                </div>

                {renderRiskCategories()}
            </div>
        </div>
    );
}

export default App;
